import React from 'react';
import './App.css';
import gameData from "./gameData.js"
import Building from "./Building"
import Resource from "./Resource"
import Clicker from "./Clicker"
class App extends React.Component 
  {
  constructor()
    {
    super();
    this.state = {
      buildings:gameData.buildingData,
      resources:gameData.resourceData,
      clickers:gameData.clickerData

    };
   this.HandleClick = this.HandleClick.bind(this);
   this.HandleChange = this.HandleChange.bind(this);
   this.buy = this.buy.bind(this);
   this.gainResource = this.gainResource.bind(this);

  }
  ComponentDidMount(prevProps, prevState)
    {
    const gameTick = setInterval(function()
      {
      this.setState(prevState=>
        {
        const buildings = prevState.buildings.slice();
        const oldResources = prevState.resources.slice();
        const newResources = oldResources.map(resouce=>
          {
          const buildingTarget = buildings.find(x=>x.name ===resource.name);  
          resouce.amount = resouce.amount + buildingTarget.count * buildingTarget.increaseRatio;
        })


        return {resources:newResources}
      })
    },100);
  }
  HandleChange(event)
    {

  }
  HandleClick(event)
    {
    const name = event.target.name;
    const target = this.state.buildings.find(building=>building.name===name);
    const countCurrent= target.count + 1;
    console.log("Test test test");

    if(target.isBuyable === true)
      {
      this.buy(target);  
      let lastBuildings = this.state.buildings.slice();
      let newBuildings = lastBuildings.map(building=>
        {
        if(building.name === name)
          building.count = countCurrent;
        return building;
      }

      )

     this.setState({buildings:newBuildings});
    }

    else{
      console.log("notBuyable");
    }
  }
  buy(target) 
    { 
    console.log("test test buy");

    const cost = Array.from(target.buyPrice);
    let lastResources = this.state.resources.slice();
    let newResources = lastResources.map(resource=>resource -= cost.filter(price=> price === resource))
    this.setState({resources:newResources});

  }
  gainResource(event)
    {
    const name = event.target.name;
    const lastResources =  this.state.resources.slice();
      const newResources = lastResources.map(resource=>
        {
        if(resource.name === name)  
          {
          resource.amount = Number(resource.amount) + Number(this.state.clickers.find(x=>x.name===name).value)
        }  
        return resource;
      });

    this.setState({resources: newResources});
    this.state.buildings.forEach(building=>
      {
      let buyLength = building.buyPrice.length;
      console.log("until new resource for,", building.name,"is", buyLength);  
      building.buyPrice.forEach(cost=>
        {
        console.log("cost is:", cost)
        let thisResource = this.state.resources.find(resource=>resource.name === cost.name);
        if(thisResource.amount >= cost.cost)
          {
          console.log("we have enough in this resouce,", thisResource.name,"using the resource:", cost.name, "we have:", cost.cost); 
          buyLength = buyLength-1;
        }
      });
      if(buyLength==0)
        {
        const oldBuildings = this.state.buildings.slice();
        const newBuildings = oldBuildings.map(buildingord=>
          {
          if(buildingord.name === building.name)
            {
            buildingord.isBuyable = true;
            if(!buildingord.isUnlocked){buildingord.isUnlocked=true}
          }
          return buildingord;
        });
        console.log(newBuildings);   
        this.setState({buildings:newBuildings})
      }
     });
  }
  render()
    {
    const buildings = this.state.buildings.map(buildingData=>
      {
      return(
        <Building 
          name ={buildingData.name}
          buyPrice={buildingData.buyPrice}
          count = {buildingData.count}
          subjectsOfIncrease = {buildingData.subjectsOfIncrease}
          isBuyable = {buildingData.isBuyable}
          sellPrice = {buildingData.sellPrice}
          isUnlocked = {buildingData.isUnlocked}
          HandleClick = {this.HandleClick}
          key={buildingData.name}

        />)
    });
    const resources = this.state.resources.map(resourceData=>
      {

        return(

        <Resource 
         name ={resourceData.name}
         resouceMax = {resourceData.resourceMax}
          isUnlocked = {resourceData.isUnlocked}
          changePerTick = {resourceData.changePerTick}
          counterTillStopped = {resourceData.countTillStopped}
          amount={resourceData.amount}
          key={resourceData.name}

        />)

    })   

    const clickers = this.state.clickers.map(clickerData=>
      {
      return(
        <Clicker
          name={clickerData.name}  
          gainResource = {this.gainResource}
          value = {clickerData.amount}
          key={clickerData.name}

        />

        )
    })




  return (

        <div className="App">
        {buildings}
        {resources}
        {clickers}
        </div>
       );
      }

}
export default App;
