import React from 'react';
import './App.css';
const gameData =
{ 
  buildingData:
    [
      {
      name:"small",
      buyPrice:1,
      count:0,
      subjectsOfIncrease:[""],
          isBuyable:false,
            sellPrice:0.5, 
            isUnlocked:true,
           },
      {
      name:"medium",
      buyPrice:10,
      count:0,
      subjectsOfIncrease:[""],
          isBuyable:false,
            sellPrice:5, 
            isUnlocked:true,
           },
          {
      name:"large",
      buyPrice:100,
      count:0,
      subjectsOfIncrease:[""],
          isBuyable:false,
            sellPrice:50, 
            isUnlocked:false,
           }
    ],

  resourceData:
    [
      {
      name:"red",
      resouceMax:100,
      isUnlocked:true,
      changePerTick:0,
          counterTillStopped:100,

           },
      {
      name:"green",
      resouceMax:100,
      isUnlocked:true,
      changePerTick:0,
          counterTillStopped:100,
           },
          {
      name:"blue",
      resouceMax:100,
      isUnlocked:false,
      changePerTick:0,
          counterTillStopped:100,
           }
    ]
};

class App extends React.Component 
  {
  constructor()
    {
    super();
    this.state = {
      buildings:gameData.buildingData,
      resources:gameData.resourceData

    };
    this.HandleClick = this.HandleClick.bind(this);
    this.HandleChange = this.HandleChange.bind(this);
  }


  render()
    {
    const buildings = this.state.buildings.map(buildingData=>
      {
      return(
        <Building 
          name ={buildingData.name}
          buyPrice={buildingData.buyPrice}
          count = {buildingData.count}
          subjectsOfIncrease = {buildingData.subjectsOfIncrease}
          isBuyable = {buildingData.isBuyable}
          sellPrice = {buildingData.sellPrice}
          isUnlocked = {buildingData.isUnlocked}
        />)
    });
    const resources = this.state.resources.map(resourceData=>
      {
      return(
        <Resource 
         name ={resourceData.name}
         resouceMax = {resourceData.resourceMax}
          isUnlocked = {resourceData.isUnlocked}
          changePerTick = {resourceData.changePerTick}
          counterTillStopped = {resourceData.countTillStopped}

        />)
    })   






  return (





        <div className="App">
        <Counter />
        <Building />

        </div>
       );
      }

}
export default App;
